        if (self::$metadata) {
            return self::$metadata;
        }
        return self::$metadata = self::processMetadata(array(
            'id' => array('label' => 'Id', 'type' => 'none', 'acl' => 'read'),
            'postingType' => array('label' => 'Operation', 'type' => 'none', 'acl' => 'read'),
            /*'postingType' => array('label' => 'Operation', 'type' => 'enum', 'acl' => 'read',
                'typeDetail' => array(
                    'type' => 'text', 'choices' => array(
                        'value' => array(
                            'Debit' => 'DEBIT', 'Credit' => 'CREDIT'
                        )
                    )),
                'form' => array('type' => 'select')
            ),*/
            // Field to control how to show the fake field "user_[...]"
            'isVatIncluded' => array('label' => 'VAT included', 'type' => 'none', 'acl' => 'edit', 'default' => true,
                'attr' => array('(change)' => 'onIsVatIncludedChange($event.target.checked)'),
                'form' => array('type' => 'boolean')
            ),
            'costMethod' => array('label' => 'Cost', 'type' => 'enum', 'acl' => 'edit',
                'attr' => array('(change)' => 'onCostMethodChange($event.target.value)'),
                'typeDetail' => array(
                    'type' => 'text', 'choices' => array(
                        'value' => array(
                            'Percentage' => 'PERCENT', 'Fixed' => 'FIXED'
                        )
                    )),
                'form' => array('type' => 'select')
            ),
            // Original field is hidden
            'costValue' => array('label' => 'Cost', 'type' => 'monetary', 'acl' => 'edit',
                'form' => array('type' => 'hidden')),
            // Fake field for user type the value (with or without VAT), because the original field is always without VAT
            'user_costValue' => array('label' => 'Cost', 'type' => 'none', 'acl' => 'edit',
                'attr' => array(
                    '(input)' => 'onCostValueEnterKey($event.target.value)',
                    '(focusout)' => 'onCostValueEnterKey($event.target.value)'
                ),
                'form' => array('type' => 'number', 'isMapped' => false),
            ),
            'costBaseValue' => array('label' => 'Cost Base Value', 'type' => 'enum', 'acl' => 'edit', 'isRequired' => false,
                'typeDetail' => array(
                    'type' => 'text', 'choices' => array(
                        'value' => array(
                            '' => '', 'Over base cost' => 'BASE_COST'
                        )
                    )),
                'form' => array('type' => 'select')
            ),
            'marginMethod' => array('label' => 'Margin', 'type' => 'enum', 'acl' => 'edit',
                'attr' => array('(change)' => 'onMarginMethodChange($event.target.value)'),
                'typeDetail' => array(
                    'type' => 'text', 'choices' => array(
                        'value' => array(
                            'Margin' => 'MARGIN', 'Markup' => 'MARKUP', 'Fixed' => 'FIXED', 'Percentage' => 'PERCENT'
                        )
                    )),
                'form' => array('type' => 'select')
            ),
            // Original field is hidden
            'marginValue' => array('label' => 'Margin Value', 'type' => 'number', 'acl' => 'edit',
                'form' => array('type' => 'hidden')),
            // Fake field for user type the value (with or without VAT), because the original field is always without VAT
            'user_marginValue' => array('label' => 'Margin Value', 'type' => 'none', 'acl' => 'edit',
                'attr' => array(
                    '(input)' => 'onMarginValueEnterKey($event.target.value)',
                    '(focusout)' => 'onMarginValueEnterKey($event.target.value)'
                ),
                'form' => array('type' => 'number', 'isMapped' => false),
            ),
            'marginBaseValue' => array('label' => 'Margin Base Value', 'type' => 'enum', 'acl' => 'edit', 'isRequired' => false,
                'typeDetail' => array(
