<?php

namespace Bck\AccountingBundle\Entity;

use AppBundle\Entity\BaseEntityRepository;

/**
 * PaymentMethodRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PaymentMethodRepository extends BaseEntityRepository
{
    static protected $metadata = null;

    /**
     * Get field metadata
     * @param $field
     * @param $attribute
     * @param $metadata (uses this metadata instead of local metadata)
     * @param $context ('view'|'form'|'none')
     * @return null
     */
    static public function getFieldMetadata($field, $attribute, $metadata = null, $context = 'none')
    {
        if (!$metadata) {
            $metadata = self::getMetadata();
        }
        return self::getLocalFieldMetadata($field, $attribute, $metadata, $context);
    }

    /**
     * Get local metadata (it needs to be implemented by children to get static variable with local metadata from parent)
     * @return mixed
     */
    protected function getLocalMetadata()
    {
        return $this->getMetadata();
    }

    /**
     * Get entity metadata
     * @return mixed
     */
    static function getMetadata()
    {
        // Process metadata only once
        if (self::$metadata) {
            return self::$metadata;
        }
        return self::$metadata = self::processMetadata(array(
            'id' => array('label' => 'Id', 'type' => 'number', 'acl' => 'read'),
            // Foreign fields, used to get the icon of the PaymentMethod
            'appIconObj' => array('table' => 'app_paymentMethod', 'label' => 'Icon', 'type' => 'object', 'acl' => 'read',
                'dependency' => 'appPaymentMethodObj', 'typeDetail' => array(
                    'table' => 'app_icon', 'bundlePrefix' => 'bck', 'bundle' => 'sysadmin', 'type' => 'none')),
            'icon' => array('table' => 'app_icon', 'field' => 'icon', 'label' => 'Icon', 'type' => 'icon',
                'acl' => 'read', 'dependency' => 'appIconObj', 'form' => array('type' => 'none')),
            'appPaymentMethodObj' => array('label' => 'Payment Method', 'type' => 'object', 'acl' => 'edit',
                'attr' => array('(onChange)' => 'onPaymentMethodChange($event)'),
                'typeDetail' => array(
                    'table' => 'app_paymentMethod', 'bundlePrefix' => 'bck', 'bundle' => 'sysadmin', 'type' => 'none',
                    'fieldInView' => 'appPaymentMethod_name'
                ),
                'form' => array('type' => 'html-select'), 'isRequired' => false
            ),
            'appPaymentMethod_name' => array('table' => 'app_paymentMethod', 'field' => 'name', 'label' => 'Payment Method',
                'type' => 'text', 'acl' => 'read', 'dependency' => 'appPaymentMethodObj', 'form' => array('type' => 'none')),
            'name' => array('label' => 'Name', 'type' => 'text', 'acl' => 'edit'),
            'description' => array('label' => 'Description', 'type' => 'textarea', 'acl' => 'edit', 'isRequired' => true,
                'attr' => array('rows' => '6')
            ),
            'forOnlinePayment' => array('label' => 'For Online Payment', 'type' => 'boolean', 'acl' => 'edit', 'default' => false),
            'insertTime' => array('label' => 'Insert Time', 'type' => 'datetime', 'acl' => 'read', 'form' => array('type' => 'none')),
            'insertUser' => array('label' => 'Insert User', 'type' => 'text', 'acl' => 'read', 'form' => array('type' => 'none')),
            'isEnabled' => array('label' => 'Enabled', 'type' => 'boolean', 'acl' => 'edit', 'default' => true,
                'view' => array('keepOriginalNormalizer' => true)
            )
        ));
    }

    /**
     * Get choices for online payment
     * @param bool $hasExecute
     * @param string $executeMethod
     * @param $options (array with queryBuilder options format)
     * @return mixed
     */
    public function getChoicesForOnlinePayment($hasExecute = true, $executeMethod = 'getResult', $options = array())
    {
        $options = array_merge(
            $options,
            array('criteria' => array(
                array(
                    'field' => 'isEnabled',
                    'expr' => 'eq',
                    'value' => true
                ),
                array(
                    'field' => 'forOnlinePayment',
                    'expr' => 'eq',
                    'value' => true
                )
            ))
        );

        $qb = $this->queryBuilder($options, false);

        if ($hasExecute) {
            $result = $this->executeQueryBuilder($qb, $executeMethod);

            if (!is_array($result)) {
                $result = array($result);
            }

            return $result;
        }

        return $qb;
    }
}