<?php

namespace BookingBundle\Entity;

use AppBundle\Service\HelperService;

/**
 * TravelBookingRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TravelBookingRepository extends BaseBookingRepository
{
    /**
     * Overrides parent
     * @return mixed
     */
    static function getMetadata()
    {
        // Process metadata only once
        if (self::$metadata) {
            return self::$metadata;
        }

        $parentMetadata = parent::getMetadata();

        $localMetadata = self::processMetadata(array(
            'placeObj' => array('label' => 'From', 'type' => 'object', 'acl' => 'read',
                'typeDetail' => array(
                    'table' => 'place', 'bundle' => 'booking', 'type' => 'none')
            ),
            'place_name' => array('table' => 'place', 'field' => 'name', 'label' => 'From (Name)', 'type' => 'text',
                'acl' => 'read', 'dependency' => 'placeObj', 'form' => array('type' => 'none')),
            'place_iata' => array('table' => 'place', 'field' => 'iataCode', 'label' => 'From', 'type' => 'text',
                'acl' => 'read', 'dependency' => 'placeObj', 'form' => array('type' => 'none')),
            'placeToObj' => array('label' => 'To', 'type' => 'object', 'acl' => 'read',
                'typeDetail' => array(
                    'table' => 'place', 'tableAlias' => 'place_to', 'bundle' => 'booking', 'type' => 'none')
            ),
            'placeTo_name' => array('table' => 'place_to', 'field' => 'name', 'label' => 'To (Name)', 'type' => 'text',
                'acl' => 'read', 'dependency' => 'placeToObj', 'form' => array('type' => 'none')),
            'placeTo_iata' => array('table' => 'place_to', 'field' => 'iataCode', 'label' => 'To', 'type' => 'text',
                'acl' => 'read', 'dependency' => 'placeToObj', 'form' => array('type' => 'none')),
            'travelBookingPaxObj' => array('label' => 'Travel Booking Pax', 'type' => 'object', 'acl' => 'read',
                'typeDetail' => array(
                    'table' => 'travelBookingPax', 'bundle' => 'booking', 'type' => 'none'),
                'form' => array('type' => 'fake')
            ),
            'clientIsPax' => array('label' => 'Client is Pax', 'type' => 'none', 'acl' => 'edit',
                'isRequired' => false, 'default' => true,
                'form' => array('type' => 'boolean', 'isMapped' => false))
        ));

        return self::$metadata = HelperService::pushIntoArray($parentMetadata, $localMetadata, 'client_avatar');
    }
}