<?php

namespace EntitiesBundle\Entity;

use AppBundle\Entity\BaseEntityRepository;

/**
 * EntityAddressRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EntityAddressRepository extends BaseEntityRepository
{
    static protected $metadata = null;

    /**
     * Get field metadata
     * @param $field
     * @param $attribute
     * @param $metadata (uses this metadata instead of local metadata)
     * @param $context ('view'|'form'|'none')
     * @return null
     */
    static public function getFieldMetadata($field, $attribute, $metadata = null, $context = 'none')
    {
        if (!$metadata) {
            $metadata = self::getMetadata();
        }
        return self::getLocalFieldMetadata($field, $attribute, $metadata, $context);
    }

    /**
     * Get local metadata (it needs to be implemented by children to get static variable with local metadata from parent)
     * @return mixed
     */
    protected function getLocalMetadata()
    {
        return $this->getMetadata();
    }

    /**
     * Get entity metadata
     * @return mixed
     */
    static function getMetadata()
    {
        // Process metadata only once
        if (self::$metadata) {
            return self::$metadata;
        }
        return self::$metadata = self::processMetadata(array(
            'id' => array('label' => 'Id', 'type' => 'number', 'acl' => 'read'),
            'entityObj' => array('label' => 'Entity', 'type' => 'object', 'acl' => 'read', 'typeDetail' => array(
                'table' => 'entity', 'bundle' => 'entities', 'type' => 'none')),
            'name' => array('label' => 'Name / Description', 'type' => 'text', 'acl' => 'edit'),
            'street1' => array('label' => 'Street 1/2', 'type' => 'text', 'acl' => 'edit'),
            'street2' => array('label' => 'Street 2/2', 'type' => 'text', 'acl' => 'edit'),
            'postCode' => array('label' => 'Post Code', 'type' => 'text', 'acl' => 'edit'),
            'city' => array('label' => 'City', 'type' => 'text', 'acl' => 'edit'),
            'region' => array('label' => 'Region', 'type' => 'text', 'acl' => 'edit'),
            'country' => array('label' => 'Country', 'type' => 'text', 'acl' => 'edit'),
            'forDocuments' => array('label' => 'For Documents', 'type' => 'boolean', 'acl' => 'edit'),
            'insertTime' => array('label' => 'Insert Time', 'type' => 'datetime', 'acl' => 'read'),
            'insertUser' => array('label' => 'Insert User', 'type' => 'text', 'acl' => 'read'),
            'isEnabled' => array('label' => 'Enabled', 'type' => 'boolean', 'acl' => 'edit', 'default' => true)
        ));
    }

    /**
     * @param $entityObj
     * @return mixed
     */
    public function getForDocumentsByEntity($entityObj)
    {
        $objArr = $this->findBy(array('entityObj' => $entityObj, 'forDocuments' => true, 'isEnabled' => true));

        // If specific address for documents is not available, get any one
        if (count($objArr) < 1) {
            $objArr = $this->findBy(array('entityObj' => $entityObj, 'isEnabled' => true));
        }

        return reset($objArr);
    }
}