<?php

namespace EntitiesBundle\Entity;

use AppBundle\Entity\BaseEntityRepository;

/**
 * EntityRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EntityRepository extends BaseEntityRepository
{
    static protected $metadata = null;

    /**
     * Get field metadata
     * @param $field
     * @param $attribute
     * @param $metadata (uses this metadata instead of local metadata)
     * @param $context ('view'|'form'|'none')
     * @return null
     */
    static public function getFieldMetadata($field, $attribute, $metadata = null, $context = 'none')
    {
        if (!$metadata) {
            $metadata = self::getMetadata();
        }
        return self::getLocalFieldMetadata($field, $attribute, $metadata, $context);
    }

    /**
     * Get local metadata (it needs to be implemented by children to get static variable with local metadata from parent)
     * @return mixed
     */
    protected function getLocalMetadata()
    {
        return $this->getMetadata();
    }

    /**
     * Get entity metadata
     * @return mixed
     */
    static function getMetadata()
    {
        // Process metadata only once
        if (self::$metadata) {
            return self::$metadata;
        }

        $localTable = lcfirst(substr(strrchr(get_called_class(), '\\'), 1, -10));

        return self::$metadata = self::processMetadata(array(
            'id' => array('label' => 'Id', 'type' => 'none', 'acl' => 'read'),
            'storeObj' => array('label' => 'Store', 'type' => 'object', 'acl' => 'read', 'typeDetail' => array(
                'table' => 'store', 'bundle' => 'admin', 'type' => 'none')),
            'code' => array('label' => 'Code', 'field' => 'CONCAT('.$localTable.'.codePrefix, '.$localTable.'.codeNumber)',
                'table' => '', 'type' => 'code', 'acl' => 'read',
                'normalizer' => array('method' => 'getCode')
            ),
            'avatar' => array('label' => 'Avatar', 'type' => 'avatar', 'acl' => 'read'),
            'title' => array('label' => 'Title', 'type' => 'enum', 'acl' => 'edit',
                'typeDetail' => array(
                    'type' => 'text', 'choices' => array(
                        'value' => array(
                            'Mr.' => 'Mr.', 'Ms.' => 'Ms.'
                        )
                    )),
                'form' => array('type' => 'select')
            ),
            'name' => array('label' => 'Name', 'type' => 'text', 'acl' => 'edit'),
            'surname' => array('label' => 'Surname', 'type' => 'text', 'acl' => 'edit'),
            'legalName' => array('label' => 'Legal Name', 'type' => 'text', 'acl' => 'edit'),
            'birthDate' => array('label' => 'Birth Date', 'type' => 'date', 'acl' => 'edit', 'isRequired' => false),
            'taxNumber' => array('label' => 'Tax Number', 'type' => 'text', 'acl' => 'edit'),
            'insertTime' => array('label' => 'Insert Time', 'type' => 'datetime', 'acl' => 'read'),
            'insertUser' => array('label' => 'Insert User', 'type' => 'text', 'acl' => 'read'),
            'isEnabled' => array('label' => 'Enabled', 'type' => 'boolean', 'acl' => 'edit', 'default' => true),
            // To join with user to avoid that users are retrieved with the remaining entities
            'userObj' => array('type' => 'object', 'acl' => 'read',
                'typeDetail' => array(
                    'table' => 'user', 'bundle' => 'admin', 'type' => 'none', 'reverseJoin' => true
                )
            ),
        ));
    }
}